mapper.insert:
INSERT INTO pos_user (
	id,pid,user_login, user_pass,
	create_time,salt,mobile
) VALUES (
	#{id},#{pid},#{userlogin}, #{userpass},
	#{createTime},#{salt},#{mobile}			
)

mapper.update:
UPDATE
	pos_user
SET
	user_pass=#{userpass}
WHERE
	user_login=#{userlogin}

UPDATE pos_user SET
mobile=#{mobile},
user_name=#{userNickname},idcard=#{idcard},realName=#{realName} WHERE user_login=#{userlogin}
